    public Loan createLoan(int amount, int duration) {
        // Calculate the interest rate based on the advertised pricing scheme
        double interestRate = (amount * duration * 0.01) / 30;

        // Create a loan object with the calculated interest rate
        Loan loan = new Loan(amount, duration, interestRate);

        // Return the loan object
        return loan;
    }

    // Display the loan details on the screen
    public void displayLoanDetails(Loan loan) {
        // Calculate the total amount to be paid back
        double totalAmount = loan.getAmount() * loan.getInterestRate();

        // Display the total amount to be paid back on the screen
        Toast.makeText(this, "Total amount to be paid back: $" + totalAmount, Toast.LENGTH_SHORT).show();
