struct NewsUpdate {
    let title: String
    let text: String
    let image: UIImage?
}

class NewsUpdateService {
    private let firestore: Firestore
    private let locationManager: CLLocationManager

    init(firestore: Firestore, locationManager: CLLocationManager) {
        self.firestore = firestore
        self.locationManager = locationManager
    }

    func getNewsUpdates(completion: @escaping ([NewsUpdate]) -> Void) {
        // Get the user's location
        let userLocation = locationManager.location

        // Query the Firestore database for news updates tailored to the user's location
        firestore.collection("news").whereField("location", contains: userLocation.latitude + "," + userLocation.longitude).getDocuments { [weak self] snapshot, error in
            if let error = error {
                completion([NewsUpdate]())
                print("Error fetching news updates: \(error.localizedDescription)")
                return
            }

            // Create a list of news updates based on the documents retrieved from Firestore
            var newsUpdates = [NewsUpdate]()
            for document in snapshot!.documents {
                let title = document["title"].stringValue
                let text = document["text"].stringValue
                let image = document["image"].stringValue
                newsUpdates.append(NewsUpdate(title: title, text: text, image: UIImage(data: Data(base64Encoded: image))!)
            }

            // Localize the news updates based on the user's location
            for update in newsUpdates {
                let location = Location(latitude: userLocation.latitude, longitude: userLocation.longitude)
                let demographic = Demographic(location: location)
                let localizedTitle = demographic.localize(update.title)
                let localizedText = demographic.localize(update.text)
                update.title = localizedTitle
                update.text = localizedText
            }

            // Provide the localized news updates to the user
            completion(newsUpdates)
        }

